mapper.select:
select count(1) from us_t_user_answer 
where question_id=#{t.questionId} 
  and user_id=#{t.userId} 
  and dest_user_id=#{t.destUserId}
  and is_delete = 0

mapper.update:
update from us_t_user_answer set is_delete =1 
where question_id=#{t.questionId} 
 and  user_id=#{t.userId} 
 and  dest_user_id=#{t.destUserId}

mapper.select:
select u.user_no as userNo, u.user_name as userName, u.team_name as teamName, u.class_name as className, IFNULL(sum(a.answer),0) as answer, u.teacher, u.team_leader as teamLeader, u.teacher_no as teacherNo from us_t_user u inner join us_t_user_answer a on a.dest_user_id = u.id inner join us_t_question q on a.question_id = q.id where a.is_delete = 0 and q.is_delete = 0 and u.is_delete = 0 and a.user_id in ( select id from us_t_user where is_delete = 0 and user_type = #{record.userType} )
and user_name like CONCAT('%', CONCAT(#{record.userName}, '%'))
and user_no like CONCAT('%', CONCAT(#{record.userNo}, '%'))
and team_name = #{record.teamName}
and class_name = #{record.className}
group by u.user_no

select u.user_name, u.class_name, u.user_no, u.team_name, ua.answer, q.question_sort from us_t_user_answer ua, us_t_user u, us_t_question q where ua.user_id = u.id and ua.question_id = q.id and u.user_type = 0 and u.is_delete = 0 and ua.is_delete = 0
and user_name like CONCAT('%', CONCAT(#{record.userName}, '%'))
and user_no like CONCAT('%', CONCAT(#{record.userNo}, '%'))
and team_name = #{record.teamName}
and class_name = #{record.className}

select u.user_name, u.class_name, u.user_no, u.team_name, ua.answer, q.question_sort from us_t_user_answer ua, us_t_user u, us_t_question q where ua.dest_user_id = u.id and ua.question_id = q.id and ua.user_id in ( select s.id from us_t_user s where s.user_type = #{record.userType} and s.is_delete = 0 ) and u.is_delete = 0 and ua.is_delete = 0
and user_name like CONCAT('%', CONCAT(#{record.userName}, '%'))
and user_no like CONCAT('%', CONCAT(#{record.userNo}, '%'))
and team_name = #{record.teamName}
and class_name = #{record.className}

mapper.update:
update us_t_user_answer set is_delete =1 
where is_delete = 0
 and  user_id=#{userId} 
 and  dest_user_id=#{destUserId}

update us_t_user_answer set is_delete =1 
where is_delete = 0
 and  user_id  =#{userId}
 and  dest_user_id in
 
 		#{item.destUserId}
