mapper.select:
select count(1) from us_t_user_answer 
where question_id=#{t.questionId} 
  and user_id=#{t.userId} 
  and dest_user_id=#{t.destUserId}
  and is_delete = 0

mapper.delete:
update from us_t_user_answer set is_delete =1 
where question_id=#{t.questionId} 
 and  user_id=#{t.userId} 
 and  dest_user_id=#{t.destUserId}

mapper.select:
select u.user_no as userNo, u.user_name as userName, u.team_name as teamName, u.class_name as className, sum(a.answer)as answer from us_t_user_answer a left join us_t_question q on a.question_id = q.id left join us_t_user u on a.user_id = u.id
left join us_t_user r on a.dest_user_id = r.id
where a.is_delete = 0 and q.is_delete = 0 and u.is_delete = 0
and a.user_id = a.dest_user_id and u.user_type = #{group}
and r.is_delete = 0 and r.user_type = #{group}
group by u.user_no
